import React from 'react';
import TodoItem from './TodoItem';
import styled from "styled-components";

const TodoList = ({ todoList, setTodoList, Delete, Toggle }) => {
  const todosList = todoList.filter((todo) => todo.Checked === false);
  const donesList = todoList.filter((todo) => todo.Checked === true);

  return (
    <AllList>
      <TodoTitle>To Do ({todosList.length})</TodoTitle>
      <ul className="todoList">
        {todosList.map((todoItem) => (
          <TodoItem
            key={todoItem.id}
            todoItem={todoItem}
            todoList={todoList}
            setTodoList={setTodoList}
            Delete={Delete}
            Toggle={Toggle}
          />
        ))}
      </ul>
      <Line />
      <DoneTitle>Done ({donesList.length})</DoneTitle>
      <ul className="doneList">
        {donesList.map((todoItem) => (
          <TodoItem
            key={todoItem.id}
            todoItem={todoItem}
            todoList={todoList}
            setTodoList={setTodoList}
            Delete={Delete}
            Toggle={Toggle}
          />
        ))}
      </ul>
    </AllList>
  );
};

const AllList = styled.div`

`;


const Line = styled.hr`
    border: 0px;
    border-top: 4px dashed pink;
`;

const Todotitle  = styled.hr`
    font-size
`;

export default TodoList;
