import React from 'react';
import TodoItem from './TodoItem';

const TodoList = ({ todoList, setTodoList, Delete }) => {
    const onToggle = useCallback(
        (id) => {
          setTodos(
            todos.map((todo) =>
              todo.id === id ? { ...todo, isCompleted: !todo.isCompleted } : todo
            )
          );
        },
        [todos]
      );

      // isCompleted 이 false인 yetTodo 배열 생성
      const fillYetTodo = () => {
        setYetTodo(todos.filter((todo) => todo.isCompleted === false));
      };

      //// isCompleted 이 true인 doneTodo 배열 생성
      const fillDoneTodo = () => {
        setDoneTodo(todos.filter((todo) => todo.isCompleted === true));
      };

  return (
    <div>
      <p className="todoTitle">To Do ({todoList.length})</p>
      <ul className="todoList">
        {todoList.map((todoItem) => (
          <TodoItem
            key={todoItem.id}
            todoItem={todoItem}
            todoList={todoList}
            setTodoList={setTodoList}
            Delete={Delete}
          />
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
