const TodoItem = ({ todoItem, todoList, setTodoList, onDelete }) => {
    const { id, text, isCompleted } = todo;
  return (
    <li>
      <span>{todoItem.text}</span>
      <button onClick={() => onDelete(id)}>🗑</button>
    </li>
  );
};

export default TodoItem;
