import React from 'react';
import TodoItem from './TodoItem';

const TodoList = ({ todoList, setTodoList, Delete }) => {
    const Toggle = (id) => {
          setTodos(
            todos.map((todo) =>
              todo.id === id ? { ...todo, Completed: !todo.Completed } : todo
            )
          );
        };

      // isCompleted 이 false인 yetTodo 배열 생성
      const fillYetTodo = () => {
        setTodoList(todoList.filter((todo) => todo.Completed === false));
      };

      //// isCompleted 이 true인 doneTodo 배열 생성
      const fillDoneTodo = () => {
        setTodoList(todoList.filter((todo) => todo.Completed === true));
      };

      // isCompleted 이 false인 yetTodo 배열 생성
   const yetTodoList = todoList.filter((todo) => todo.isCompleted === false);

   //isCompleted 이 true인 doneTodo 배열 생
   const doneTodoList = todoList.filter((todo) => todo.isCompleted === true);


  return (
    <div>
      <p className="todoTitle">To Do ({todoList.length})</p>
      <ul className="todoList">
        {yetTodoList.map((todoItem) => (
          <TodoItem
            key={todoItem.id}
            todoItem={todoItem}
            todoList={todoList}
            setTodoList={setTodoList}
            Delete={Delete}
            Toggle={Toggle}
          />
        ))}
      </ul>
      <p className="doneTitle">Done ({doneTodoList.length})</p>
      <ul className="todoList">
        {doneTodoList.map((todoItem) => (
          <TodoItem
            key={todoItem.id}
            todoItem={todoItem}
            todoList={todoList}
            setTodoList={setTodoList}
            Delete={Delete}
            Toggle={Toggle}
          />
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
