import React, { useState, useCallback } from 'react';
import Input from './components/Input';
import TodoList from './components/TodoList';
import DoneList from './components/DoneList';

function App() {
  const [todoList, setTodoList] = useState([]);
  const [todos, setTodos] = useState([]);
  const [dones, setDones] = useState([]);


  const Toggle= (id)=>{
    setTodoList(todoList.map((todo)=>todo.id === id ? { ... todo, Checked: !todo.Checked} : todo));
    console.log('toggled');
    };

  const todosList = () => {
    setTodos(todoList.filter((todo) => todo.Checked === false));
  };

  const donesList = () => {
    setDones(todoList.filter((todo) => todo.Checked === true));
  };

  return (
    <div>
      <h1>투두리스트</h1>
      <Input todoList={todoList} setTodoList={setTodoList} />
      <TodoList todoList={todoList} setTodoList={setTodoList} Toggle={Toggle}/>
      <DoneList />
    </div>
  );
}

export default App;
