import React from 'react';
import TodoItem from './TodoItem';

const TodoList = ({ todoList, setTodoList, Delete, checkedList }) => {

  return (
    <div>
      <p className="todoTitle">To Do ({todoList.length})</p>
      <ul className="todoList">
      {todoList && // todoList가 있을때만 출력
        todoList.map((todoItem) => {
          // checkedList 값에 따라 '할 일 목록' 또는 '완료한 목록'을 출력
          if (checkedList !== todoItem.checked) return null;

          return (
          <TodoItem
            key={todoItem.id}
            todoItem={todoItem}
            todoList={todoList}
            setTodoList={setTodoList}
            Delete={Delete}
          />
        )})}
      </ul>
      <p className="doneTitle">Done ({todoList.length})</p>
    </div>
  );
};

export default TodoList;
